INSERT INTO Node (NodeID, Author, CreationDate, ModificationDate, NodeType, OriginalID, ExtendedNodeType, Label, Detail, CurrentStatus, LastModAuthor) VALUES ('1270111357816460805','kcby',1354900367322,1358419814682,3,'','','Make C a component based platform with plug-ins','Jack Park, in chat\n\nFrom my perspective, turn Compendium into a component-based platform in which its functionality can be extended with plug-in features. There are at least two models for that: one is embedded in http://Eclipse.org, the other is embedded in http://Protege.stanford.edu',0,'compendiumng')
