INSERT INTO Node (NodeID, Author, CreationDate, ModificationDate, NodeType, OriginalID, ExtendedNodeType, Label, Detail, CurrentStatus, LastModAuthor) VALUES ('1270111357816461297','kcby',1354738409725,1358337664933,3,'','','Web based','Running on a web server, works like an improved message board.\n\nFrom Jack Park in chat:\nWeb functionality seems to be popular; I once built a simple variant of Compendium that was embedded inside a Jabber client; a facilitator (or anyone) could add nodes based on a Jabber conference room.',0,'compendiumng')
