INSERT INTO Node (NodeID, Author, CreationDate, ModificationDate, NodeType, OriginalID, ExtendedNodeType, Label, Detail, CurrentStatus, LastModAuthor) VALUES ('1270111358338454929','compendiumng',1358338454929,1358515033435,9,'','','Cog Nexus Meeting','David Constant via email to KCBY:\n\nAbility to have different arrangements by node; e.g. be able to have child nodes aligned horizontally or in block, to minimize space; then when need to build out from one of these nodes, be able to change the alignments with control-key combinations (like the current Ctl-R)',0,'compendiumng')
INSERT INTO Node (NodeID, Author, CreationDate, ModificationDate, NodeType, OriginalID, ExtendedNodeType, Label, Detail, CurrentStatus, LastModAuthor) VALUES ('1270111358338455011','compendiumng',1358338455011,1358514504619,9,'','','Cog Nexus Meeting','David Constant via email to KCBY:\n\nSupport pen/touch interface better. E.g. line gestures create node types and connections. E.g. drawing a line to right ending in "a" generates connected answer, ending in "q" generates a connected question',0,'compendiumng')
INSERT INTO Node (NodeID, Author, CreationDate, ModificationDate, NodeType, OriginalID, ExtendedNodeType, Label, Detail, CurrentStatus, LastModAuthor) VALUES ('1270111358338455186','compendiumng',1358338455186,1358513470219,9,'','','Cog Nexus Meeting','KCBY thought post meeting.\n\nMany people are uncomfortable with using computers in meetings, but can take notes quickly using indented text to represent the links between ''nodes'' and some minimal indicators of node type (for example, ? at end of the line of text for Question), + at beginning of the text for Pro, - at beginning for Con, * at beginning for Decision.)\n\nIn some corporate enviroments, these handwritten notes can be given to adminstrative support to be converted to MS Word or other text documents.  Or they can easily be transcribed by the person who uses C but doesn''t want to use it in a meeting. \n\nProvided the rules were followed, a simple import process should be able to convert these outlines to Compendium Issue Maps.',0,'compendiumng')
